import React, { useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";

import { startPokemon } from "../components/redux/actions";

const pokemonCardStyles = {
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
  flexDirection: "column",
};

function PokemonsGeneral() {
  let { id } = useParams();
  const { pokemon, isLoading, isError } = useSelector(
    (state) => state.specificPokemon
  );

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(startPokemon(id));
  }, []);

  return (
    <div>
      <Link to="/">
        <h2>Home</h2>
      </Link>
      {isError ? (
        <h2>An error occured, sorry</h2>
      ) : isLoading ? (
        <h3>Loading...</h3>
      ) : (
        <>
          <div>{pokemon.name}</div>
          <div>
            {pokemon.types &&
              pokemon.types.map((type) => {
                return <h2>{type}</h2>;
              })}
          </div>
          <div>{pokemon.supertype}</div>
          <div>{pokemon.subtype}</div>
          <div>{pokemon.hp}</div>
          <div>
            {pokemon.retreatCost &&
              pokemon.retreatCost.map((cost) => {
                return <h2>{cost}</h2>;
              })}
          </div>
          <div>{pokemon.covertedRetreatCost}</div>
          <div>{pokemon.rarity}</div>
          <div>{pokemon.series}</div>
          <div>{pokemon.set}</div>
          <AttackDesign attacks={pokemon.attacks} />
        </>
      )}
    </div>
  );
}

function AttackDesign({ attacks }) {
  return (
    <div>
      {attacks &&
        attacks.map((attack) => {
          return (
            <div
              style={{
                display: "flex",
                alignItems: "center",
                flexDirection: "column",
              }}
            >
              <div
                style={{
                  display: "flex",
                  alignItems: "center",
                  flexDirection: "column",
                }}
              >
                <div
                  style={{
                    display: "flex",
                  }}
                >
                  {" "}
                  Costs:
                  {attack.cost.map((cost) => {
                    return <h4>{cost}</h4>;
                  })}
                </div>
                <h4>{attack.name}</h4>
              </div>
              <h5>{attack.text}</h5>
            </div>
          );
        })}
    </div>
  );
}

export default PokemonsGeneral;
