import React, { useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";

import { startPokemon } from "../components/redux/actions";

const pokemonCardStyles = {
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
  flexDirection: "column",
};

function PokemonsGeneral() {
  let { id } = useParams();
  const { pokemon, isLoading, isError } = useSelector(
    (state) => state.specificPokemon
  );

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(startPokemon(id));
  }, []);

  return (
    <div>
      <Link to="/">
        <h2>Home</h2>
      </Link>
      {isError ? (
        <h2>An error occured, sorry</h2>
      ) : isLoading ? (
        <h3>Loading...</h3>
      ) : (
        <>
          <div>{pokemon.name}</div>
          <div>
            {pokemon.types &&
              pokemon.types.map((type) => {
                return <h2>{type}</h2>;
              })}
          </div>
          <div>{pokemon}</div>
          <div>{pokemon.supertype}</div>
          <div>{pokemon.subtype}</div>
          <div>{pokemon.hp}</div>
          <div>
            {pokemon.retreatCost &&
              pokemon.retreatCost((cost) => {
                return <h2>{cost}</h2>;
              })}
          </div>
          <div>{pokemon}</div>
          <div>{pokemon}</div>
          <div>{pokemon}</div>
          <div>{pokemon}</div>
          <div>{pokemon}</div>
          <div>{pokemon}</div>
          <div>{pokemon}</div>
        </>
      )}
    </div>
  );
}

export default PokemonsGeneral;
